#include <bits/stdc++.h>
#include "gmp.h"

using namespace std;

int main()
{
    mpz_t n, p, q, e, d, phi, p_1, q_1, cipher, plain, plain_hex;

    mpz_init_set_str(n, "", 10);
    mpz_init_set_str(p, "13407807929942597099574024998205846127479365820592393377723561443721764030073662768891111614362326998675040546094339320838419523375986027530441562135724301", 10);
    mpz_init_set_str(q, "13407807929942597099574024998205846127479365820592393377723561443721764030073778560980348930557750569660049234002192590823085163940025485114449475265364281", 10);

    mpz_init_set_str(e, "65537", 10);
    mpz_init(d);
    mpz_init_set_str(cipher, "", 10);
    mpz_init(plain);
    mpz_init(plain_hex);

    mpz_init(phi);
    mpz_init(p_1);
    mpz_init(q_1);

    mpz_sub_ui(p_1, p, 1);
    mpz_sub_ui(q_1, q, 1);
    mpz_mul(phi, p_1, q_1);
    mpz_invert(d, e, phi);
    
    mpz_powm(plain, cipher, d, n);
    gmp_printf("d = %Zd\n", d);
    gmp_printf("plain = %Zd\n", plain);
    return 0;
}